mapper.update:
update 
	t_auth_user 
set 
  	status=#{status}
where 
  	id = #{id}

mapper.select:

			select 
			id, create_time, email,update_time,login_name, memo, password,phone,  version,status,user_type,level
      from t_auth_user where id=#{id}


		select 
			id, create_time, email,update_time,login_name, memo, password,phone,  version,status,user_type,level
      from t_auth_user where  user_type=1 and id=#{id}
