mapper.update:
	update
	     t_sysmgr_version_config
	set update_time = now()

		, name = #{name}


		, version_num = #{versionNum}


		, source_code = #{sourceCode}


		, is_fource = #{isFource}


		, feature = #{feature}


		, url = #{url}


		, is_deleted = #{isDeleted}


		, create_id = #{createId}


		, update_id = #{updateId}

   where id = #{id}

mapper.select:



    	   AND  source_code = #{sourceCode}


    	   AND  version_num = #{versionNum}


	order by create_time desc 
	limit #{start},#{limit}

	select 
		count(id)
	from 
	t_sysmgr_version_config


    	   AND  source_code = #{sourceCode}


    	   AND  version_num = #{versionNum}
