mapper.select:
select a.id,a.category_code,a.sku_code,a.show_order from t_sku_category_relation as a where
a.category_code = #{categoryCode}
order by a.show_order desc
LIMIT #{start},#{limit}

select a.id,a.category_code,a.sku_code,a.show_order from t_sku_category_relation as a inner join t_sku b on a.sku_code = b.code left JOIN t_sku_hot c on b.code = c.sku_code inner join t_sku_inventory d on b.code = d.sku_code
d.available_qty > 0
and a.category_code = #{categoryCode}
and a.sku_code = #{skuCode}
and b.status = #{status}
order by a.show_order asc
order by b.onshelvestime desc , a.show_order asc
order by c.count desc, a.show_order asc
order by b.current_price asc, a.show_order asc
order by b.current_price desc, a.show_order asc
LIMIT #{start},#{limit}
