mapper.select:
select s.id, s.member_name, s.mobile, s.sms_type_id, s.sms_content, s.plan_time, s.send_time, s.status, s.create_time, s.update_time, s.create_id, s.update_id, s.send_level, s.source_code, s.is_deleted, s.failure_reason, s.sms_send_flag,st.sms_name from t_sms s inner join t_sms_type_config st on s.sms_type_id = st.id
AND member_name LIKE "%"#{memberName}"%"
AND mobile LIKE "%"#{mobile}"%"
AND source_code = #{sourceCode}
AND sms_type_id = #{smsTypeId}
and DATE_FORMAT(s.send_time,'%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{createTimeFrom}, '%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(s.send_time,'%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{createTimeTo}, '%Y-%m-%d %H:%i:%s')
order by s.create_time desc LIMIT #{start},#{limit}

select count(s.id) from t_sms s inner join t_sms_type_config st on s.sms_type_id = st.id
AND member_name LIKE "%"#{memberName}"%"
AND mobile LIKE "%"#{mobile}"%"
AND source_code = #{sourceCode}
AND sms_type_id = #{smsTypeId}
